	       $("#searchError").css('display','none');	         
	       if(q=='Search for stores'){ 
		    $('[name=q]').val('');
	       }	      
	       if(q=='Search for stores'){  
		    $("#searchError").css('display','block');
		    return false;
	       }
	       if(q==''){
		    $("#searchError").css('display','block');
		    return false;
	       }
	       
	       $.ajaxSetup({async:false});
	       $.ajax({		    
		    type: 'GET',
		    url: 'getSearchResult.php/q',
