		    beforeSend: function() {
			 //$("#emailformresponse").text("");
		    },	
		    success: function(data) {			 
			 if(data!="1:2"){
			      var stringArray = data.split(":");			
			      var url = '/'+stringArray[0]+ext;//alert(stringArray[0]);
			      $('[name=q]').val(stringArray[0]);

			      $('[name=storeArray]').val(stringArray[1]);
                             
			      $("#siteSearch").attr('action', url);
			 } else {
			      $("#siteSearch").attr('action', '/');
			 }
		    },
	       });
	  });	  
     });    
