<script>
function validFtrForm(objForm){
     var ftrFormFlag = true;
     
     if(objForm.email.value!='' && objForm.email.value=='Email'){
	  document.getElementById('ftrFormEmailError').innerHTML = "<span style='color:#fff;font-size:11px'>Please enter email id.</span>";
	  ftrFormFlag = false;
     }
     if(objForm.msg.value!='' && objForm.msg.value=='Message'){
	  document.getElementById('ftrFormMsgError').innerHTML = "<span style='color:#fff;font-size:11px'>Please enter message.</span>";
	  ftrFormFlag = false;
     }
     if(!ftrFormFlag){	  
	  return ftrFormFlag;
     } else {
	  var xmlHttp = getXMLHTTPObject();
	  xmlHttp.onreadystatechange=function() {
	       if(xmlHttp.readyState==4) {
		    var dataVal = xmlHttp.responseText;
		    alert(dataVal);
		    document.ftrForm.reset();
	       }
	  };
	  var url = "addemailftrform.php?email="+objForm.email.value+"&msg="+objForm.msg.value;
	  xmlHttp.open("GET",url ,true);
	  xmlHttp.send(null);	  
     }
}
</script>
